  //               style={{ margin: "10px" }}
  //             >
                // <option value="7" selected="selected">
                //   Candlestick
                // </option>
                // <option value="8">Revolver</option>
                // <option value="9">Knife</option>
                // <option value="10">Pipe</option>
                // <option value="11">Rope</option>
                // <option value="12">Wrench</option>
  //             </select>
  //             <br />
  //             <button
  //               name="sgbtn"
