  //             </button>
  //             <br />
  //             &nbsp;&nbsp;&nbsp;
  //           </span>
  //         </div>
  //         <p>{this.props.location}</p>

  render() {
    return (
      <div style={{ textAlign: "center" }}>
        <div
          style={{
            textAlign: "center",
            width: "100%",
            height: "150px",
            margin: "10px",
          }}
        >
          <div
            style={{
              textAlign: "center",
              width: "300px",
              "Min-Height": "150px",
              "border-width": "5px",
              "border-color": "red",
              "border-style": "outset",
              margin: "0 auto",
              position: "relative",
            }}
          >
            <span>
              <h3>Suggestion / Accusation Box</h3>
              <select
                name="GuessedUser"
                id="GuessedUser"
                style={{ margin: "10px", marginLeft: "0px" }}
              >
                <option value="1" selected="selected">
                   Miss Scarlet
                 </option>
                <option value="2">Mr. Green</option>
                 <option value="3">Colonel Mustard</option>
                 <option value="4">Prof. Plum</option>
                 <option value="5">Mrs. Peacock</option>
                 <option value="6">Mrs. White</option>
              </select>
              <select
                name="GuessedWeapon"
                id="GuessedWeapon"
                style={{ margin: "10px" }}
              >
                <option value="7" selected="selected">
                  Candlestick
                </option>
                <option value="8">Revolver</option>
                <option value="9">Knife</option>
                <option value="10">Pipe</option>
                <option value="11">Rope</option>
                <option value="12">Wrench</option>
              </select>
              <br />
              <button
                name="sgbtn"
                id="sgbtn"
                onClick={() => {
                  this.suggestionClicked(
                    document.getElementById("GuessedUser").value,
                    document.getElementById("GuessedWeapon").value
                  );
                }}
              >
                Make Suggestion
              </button>
              &nbsp;&nbsp;&nbsp;
              <button
                name="acbtn"
                id="acbtn"
                onClick={() => {
                  this.accusationClicked(
                    document.getElementById("GuessedUser").value,
                    document.getElementById("GuessedWeapon").value
                  );
                }}
              >
                Make Accusation
              </button>
              <br />
              &nbsp;&nbsp;&nbsp;
            </span>
          </div>
        </div>
        <br />
        <button
          onClick={() => {
            this.ShowSuggestionWindow("This is the suggestion.");
          }}
        >
          Respond To Suggestion
        </button>{" "}
        {/*Testing*/}
        {/*Suggestion Response START*/}
        <div
          id="suggestionresponsediv"
          style={{
            position: "absolute",
            top: "0",
            left: "0",
            width: "100%",
            height: "150%",
            "background-color": "grey",
            opacity: ".98",
            "z-index": "1000",
            display: "none",
          }}
        >
          <br />
          <br />
          <h2 name="suggestiondetail" id="suggestiondetail"></h2>
          <select
            name="MyCardsType"
            id="MyCardsType"
            style={{ margin: "10px", marginLeft: "0px" }}
            onChange={() => {
              this.updateDisproveUI();
            }}
          >
            <option value="-1" selected="selected">
              I don't have any of the suggested cards
            </option>
            <option value="0">I have suggested Player card : </option>
            <option value="1">I have suggested Weapon card : </option>
            <option value="2">I have suggested Room card : </option>
          </select>
          <select
            name="ResponseUser"
            id="ResponseUser"
            style={{ margin: "10px", marginLeft: "0px", display: "none" }}
          >
            <option value="scarlet" selected="selected">
              Miss Scarlet
            </option>
            <option value="green">Mr. Green</option>
            <option value="mustard">Colonel Mustard</option>
            <option value="plum">Prof. Plum</option>
            <option value="peacock">Mrs. Peacock</option>
            <option value="white">Mrs. White</option>
          </select>
          <select
            name="ResponseWeapon"
            id="ResponseWeapon"
            style={{ margin: "10px", display: "none" }}
          >
            <option value="candlestick" selected="selected">
              candlestick
            </option>
            <option value="revolver">revolver</option>
            <option value="knife">knife</option>
            <option value="pipe">pipe</option>
            <option value="rope">rope</option>
            <option value="wrench">wrench</option>
          </select>
          <select
            name="ResponseRoom"
            id="ResponseRoom"
            style={{ margin: "10px", display: "none" }}
          >
            <option value="Kitchen" selected="selected">
              Kitchen
            </option>
            <option value="Ballroom">Ballroom</option>
            <option value="Conservatory">Conservatory</option>
            <option value="Dining">Dining Room</option>
            <option value="Cellar">Cellar</option>
            <option value="Billiards">Billiards Room</option>
            <option value="Library">Library</option>
            <option value="Study">Study</option>
            <option value="Lounge">Lounge</option>
          </select>
          <button
            onClick={() => {
              this.sendSuggestionResponse();
            }}
          >
            Repond
          </button>
        </div>
        {/*Suggestion Response START*/}
        <br />
        <br />
        <br />
      </div>
    );
  }
}

export default Box;
