import React from "react";
import Relaxed from "./Music/relaxed.wav";
import Electronic from "./Music/electronic.wav";
import Marimba from "./Music/marimba.wav";
import Dreamy from "./Music/dreamy.wav";
import Dreamy_2 from "./Music/dreamy_2.wav";






class Music extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            play: false,
            pause: true,
            // selectValue: Relaxed,
        } 

        //default
        this.audio = new Audio(Relaxed);

        this.audio.addEventListener('ended', function () {
            this.currentTime = 0;
            this.play();
        }, false);

    }

    play = () => {
        this.setState({ play: true, pause: false })
        this.audio.play();
    }

    pause = () => {
        this.setState({ play: false, pause: true })
        this.audio.pause();
    }


    getInitialState = () => {
        return { selectValue: 'Relaxed' };
    }

    handleChange = (e) => {
        this.setState({ selectValue: e.target.value });
    }


    // React.render(<FruitSelector name="World" />, document.body);

    render() {
        var message='You selected '+this.state.selectValue;

        return (
            <div>
                <button onClick={this.play}>Play</button>
                <button onClick={this.pause}>Pause</button>

                <select value={this.state.selectValue} onChange={this.handleChange}>
                    <option value="Relaxed">Relaxed</option>
                    <option value="Electronic">Electronic</option>
                    <option value="Marimba">Marimba</option>
                    <option value="Dreamy">Dreamy</option>
                    <option value="Dreamy_2">Dreamy_2</option>
                </select>
                <p>{message}</p>
            </div>




        );
    }


}




