  constructor(props) {
    super(props);
    this.state = {

      // Gameboard grid
      grid: startGrid,

      // Tracks all player and weapon locations
      locations: startLocations,

      // Tracks current player location
      currentX: 0,
      currentY: 0,

      // Used for movement turn
      validOptions: [],
      movementTurn: false,

    };
    this.handleOnClick = this.handleOnClick.bind(this);
    this.noMovementClick = this.noMovementClick.bind(this);
    this.suggestionClicked = this.suggestionClicked.bind(this);
    this.accusationClicked = this.accusationClicked.bind(this);
    this.dims = [
      parseFloat(500 / this.state.grid.length),
      parseFloat(500 / this.state.grid[0].length),
    ];
  }

  state = {
    locations: currentXY,
  };

  suggestionClicked(a, b) {
    //alert(a);
    //alert(b);
    const cx = this.state.locations[this.props.player_id].currentX;
    const cy = this.state.locations[this.props.player_id].currentY;
    //alert(JSON.stringify(this.state.grid[cx][cy]));
    //alert(this.state.grid[cx][cy].roomName);

    var roomName = "";
    switch (this.state.grid[cx][cy].roomId) {
      case "11":
        roomName = "kitchen";
        break;
      case "12":
        roomName = "ballroom";
        break;
      case "13":
        roomName = "conservatory";
        break;
      case "21":
        roomName = "dinning";
        break;
      case "22":
        roomName = "hall";
        break;
      case "23":
        roomName = "billiard";
        break;
      case "31":
        roomName = "library";
        break;
      case "32":
        roomName = "study";
        break;
      case "33":
        roomName = "lounge";
        break;
      default:
        alert("You need to be in a room!");
        break;
    }
    if (roomName == "") return;
    var playerInput = "You are Suggesting : " + roomName + " " + a + " " + b;
    alert(playerInput);
    //this.setState({ inputs : [playerInput, ...this.state.inputs]})
    makeSuggestion(roomName, a, b);
  }

  accusationClicked(a, b) {
    //alert(a);
    //alert(b);
    const cx = this.state.locations[this.props.player_id].currentX;
    const cy = this.state.locations[this.props.player_id].currentY;
    //alert(JSON.stringify(this.state.grid[cx][cy]));
    //alert(this.state.grid[cx][cy].roomName);
    var roomName = "";
    switch (this.state.grid[cx][cy].roomId) {
      case "11":
        roomName = "kitchen";
        break;
      case "12":
        roomName = "ballroom";
        break;
      case "13":
        roomName = "conservatory";
        break;
      case "21":
        roomName = "dinning";
        break;
      case "22":
