import React from "react";

var FACETS = [
  {
    name: "CPU Socket Type",
    facetItems: [
      { checked: false, name: "LGA 1150", count: "137" },
      { checked: true, name: "LGA 1151", count: "53" },
      { checked: false, name: "LGA 1155", count: "122" },
    ],
  },
  {
    name: "Manufacturer",
    facetItems: [
      { checked: false, name: "Intel", count: "733" },
      { checked: true, name: "AMD", count: "682" },
    ],
  },
  {
    name: "Price",
    facetItems: [
      { checked: false, name: "$0 - $10", count: "76" },
      { checked: false, name: "$10 - $20", count: "22" },
      { checked: false, name: "$25 - $50", count: "48" },
      { checked: true, name: "$50 - $75", count: "29" },
      { checked: false, name: "$75 - $100", count: "24" },
      { checked: false, name: "$100 - $200", count: "37" },
    ],
  },
];

class Navbar extends React.Component {
  render() {
    return (
      <div id="masthead-positioner">
        <div id="masthead-container">
          <div id="masthead">
            <div id="logo">
              <a id="left-drawer-toggle" className="active" href="#">
                <i className="fa fa-bars"></i>
              </a>
              <div id="app-title">
                <span className="words rig">lo</span>
                <span className="words config">go</span>
              </div>
            </div>
            <div id="signin">
              <span id="avatar">
                <a href="">
                  <img src="http://placehold.it/40x40" />
                </a>
                <span className="username-badge">smonsen</span>
              </span>
              <a id="right-menu-toggle" className="" href="#">
                <i className="fa fa-hdd-o"></i>
              </a>
            </div>
            <div id="masthead-content">
              <form id="masthead-search" action="#">
                <button id="search-btn" type="submit" dir="ltr" tabIndex="2">
                  <i className="fa fa-search"></i>
                </button>
                <div id="masthead-search-terms">
                  <input
                    type="text"
                    id="masthead-search-term"
                    placeholder="Search for rigs and components"
                  />
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

class FacetItem extends React.Component {
  render() {
    return (
      <dd>
        <input
          type="checkbox"
          defaultChecked={this.props.facetItem.checked ? true : false}
        />
        <label>{this.props.facetItem.name}</label>
        <span className="facet-count">({this.props.facetItem.count})</span>
      </dd>
    );
  }
}

class FacetGroup extends React.Component {
  constructor(props) {
    super(props);
    this.state = { collapsed: false };
  }

  handleFacetCollapseToggle(e) {
    // console.log('e.target', e.target);
    // console.log('e.target', ReactDOM.findDOMNode(e.target));
    // console.log('e.target.parentElement', e.target.parentElement);
    // console.log('e.target.parentNode', e.target.parentNode);
    // console.log(this.refs.facet);
    if (this.state.collapsed == true) {
      this.setState({ collapsed: false });
    } else {
      this.setState({ collapsed: true });
    }
  }

  render() {
    return (
      <div>
        <dt onClick={this.handleFacetCollapseToggle.bind(this)}>
          <span>{this.props.facet.name}</span>
        </dt>
        {this.props.facet.facetItems.map((facetItem, idx) => {
          return <FacetItem key={facetItem + idx} facetItem={facetItem} />;
        })}
      </div>
    );
  }
}

class FacetsContainer extends React.Component {
  render() {
    return (
      <form>
        <div id="facets">
          {this.props.facets.map((facet, idx) => {
            return <FacetGroup key={facet + idx} facet={facet} />;
          })}
        </div>
      </form>
    );
  }
}

class LeftDrawer extends React.Component {
  render() {
    return (
      <div id="left-drawer" className="open">
        <FacetsContainer facets={FACETS} />
      </div>
    );
  }
}

export class Masthead extends React.Component {
  constructor(props) {}
  render() {
    return (
      <div id="app">
        <Navbar />
        <div id="page" className="clearfix">
          <LeftDrawer />
        </div>
      </div>
    );
  }
}

export default Gameboard;
