import React from "react";
import CardMapper from "./CardMapper";
import "./PlayerHand.css";

class PlayerHand extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cards: this.props.cards,
    };
  }
  state = {
    cards: [],
  };
  updatePlayerHand = this.state.cards.map((card) => {
    CardMapper.generateIdMap();
    var imgLocation = "./CardImages/" + CardMapper.getCardImgById(card);
    return (
      <li>
        <div class="block">
          <img src={imgLocation} />
        </div>
      </li>
    );
  });

  render() {
    return (
      <div class="playerHandFrame">
        <ul class="hs center">{this.updatePlayerHand}</ul>
        <ul class="hs center">
          <li>
            <div class="block">
              <img src="./CardImages/BallRoom.png" />
            </div>
          </li>
          <li>
            <div class="block">
              <img src="./CardImages/Mr_Green.png" />
            </div>
          </li>
          <li>
            <div class="block">
              <img src="./CardImages/Revolver.png" />
            </div>
          </li>
          <li>
            <div class="block">
              <img src="./CardImages/Mrs_Peacock.png" />
            </div>
          </li>
          <li>
            <div class="block">
              <img src="./CardImages/Prof_Plum.png" />
            </div>
          </li>
        </ul>
      </div>
    );
  }
}

export default PlayerHand;
