import React from "react";

class UserInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      response: "",
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.makeSuggestion = this.makeSuggestion.bind(this); // Maybe add later?
  }
  // Note: This sets the state for all type events. Even individual entries.
  // Maybe we should move the "setState" to the submit button event?
  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    if (typeof this.state.value != "undefined") {
      alert("Please type an action.");
      return;
    }

    // Notify User that their action was registered.
    alert("Player action registered: " + this.state.value);

    // Added for demonstration purposes:
    this.parseMessage(event.target.value);
    event.preventDefault();
  }

  makeSuggestion() {
    const _this = this;
    let jsonBody = '{"Test":"Test"}';
    //console.log(jsonBody);
    fetch(
      "http://node-express-env.eba-m3dz2nxw.us-east-2.elasticbeanstalk.com/test",
      {
        method: "GET",
        withCredentials: true,
        //credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
        //body: jsonBody,
      }
    )
      .then((response) => response.json())
      .then((jsonResponse) => {
        console.log("Response: " + JSON.stringify(jsonResponse));
        _this.setState({
          response: JSON.stringify(jsonResponse),
        });
      });
    console.log("Response: " + this.state.response);
  }

  parseMessage(playerInput) {
    let parsedPlayerInput = playerInput.split(/\s+/);
    console.log("Parsed Input: " + parsedPlayerInput);
  }

  render() {
    return (
      <React.Fragment>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            size="50"
            value={this.state.value}
            onChange={this.handleChange}
          />
          <input type="submit" value="Submit" />
        </form>
        <div>Response: {this.state.response}</div>
      </React.Fragment>
    );
  }
}
export default UserInput;
