import { useEffect } from 'react';


export function useGameboardMessage() {

    let message = "";

    useEffect(() => {
      const handler = event => {
        // const data = JSON.parse(event.data)
        message = event.data;
        alert(message);
        // alert(requestedMove)

      }

      window.addEventListener("message", handler)
    }, [])

    return message;
  }
