import React from "react";
import {
  makeMovement,
  makeSuggestion,
  makeAccusation,
  makeDisprove,
} from "./ClientManager";

var uniqueIDs = [
  //0
  { type: "character", name: "Miss Scarlet", image: "C1" },
  //1
  { type: "character", name: "Mr. Green", image: "C2" },
  //2
  { type: "character", name: "Colonel Mustard", image: "C3" },
  //3
  { type: "character", name: "Prof. Plum", image: "C4" },
  //4
  { type: "character", name: "Mrs. Peacock", image: "C5" },
  //5
  { type: "character", name: "Mrs. White", image: "C6" },
  //6
  { type: "weapon", name: "Candlestick", image: "W1" },
  //7
  { type: "weapon", name: "Revolver", image: "W4" },
  //8
  { type: "weapon", name: "Knife", image: "W2" },
  //9
  { type: "weapon", name: "Pipe", image: "W3" },
  //10
  { type: "weapon", name: "Rope", image: "W5" },
  //11
  { type: "weapon", name: "Wrench", image: "W6" },
  //12
  { type: "room", name: "Study", roomId: 11 },
  //13
  { type: "room", name: "Hall", roomId: 12 },
  //14
  { type: "room", name: "Lounge", roomId: 13 },
  //15
  { type: "room", name: "Dinning Room", roomId: 23 },
  //16
  { type: "room", name: "Billiard Room", roomId: 22 },
  //17
  { type: "room", name: "Library", roomId: 21 },
  //18
  { type: "room", name: "Conservatory", roomId: 31 },
  //19
  { type: "room", name: "Ballroom", roomId: 32 },
  //20
  { type: "room", name: "Kitchen", roomId: 33 },
];

class LoginPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      response: "",
      inputs: [],
      firstName: "",
      lastName: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  // handleChange(event) {
  //   this.setState({ value: event.target.value });
  // }

  handleChange(evt) {
    const value = evt.target.value;
    this.setState({
      ...this.state,
      [evt.target.name]: value
    });
    console.log(JSON.stringify(this.state))
  }

  handleSubmit(event) {
    if (this.state.value === "") {
      alert("Don't forget to type an action!");
      return;
    }

    // Notify User that their action was registered.
    let userInput = this.state.value;
    this.setState({ response: userInput });

    alert("User Input: " + this.state.value);
    console.log("User Input: " + this.state.value);
    this.setState({
      value: "",
    });
    event.preventDefault();
  }

  createSelectItems(type) {
    let items = uniqueIDs.map((item, i) => {
      return (
        item.type == type && (
          <option key={i} value={i}>
            {item.name}
          </option>
        )
      );
    }, this);
    return items;
  }

  render() {
    return (
      <div>
        <h4>Welcome!</h4>
        <input className="input" type="text" placeholder="Username" />


        <div className="form-group">
          <input className="input" type="text" placeholder="Email" />
        </div>
        <div>
          <input className="input" type="password" placeholder="Password" />
        </div>
        <div className="form-group">
          <button className="btn" type="submit">
            Log In
          </button>
        </div>
        <div className="form-group">
          <form className="btn" onSubmit={this.handleSubmit}>
            <input value={this.state.value} onChange={this.handleChange} />
            <input value={this.state.value} onChange={this.handleChange} />
            <select>{this.createSelectItems("character")}</select>
            <input type="submit" value="Submit" />
          </form>
        </div>

        <form>
          <div>
        <label for="email">Email:</label>
      <input type="email" class="form-control" id="email" placeholder="Enter email" name="email"/>
    </div>
    <div class="form-group">
      <label for="pwd">Password:</label>
      <input type="password" class="form-control" id="pwd" placeholder="Enter password" name="pwd"/>
    </div>
    <button type="submit" class="btn btn-default">Submit</button>
        </form>

        <form>
    <label>
      First name
      <input
        type="text"
        name="firstName"
        value={this.state.firstName}
        onChange={this.handleChange}
      />
    </label>
    <label>
      Last name
      <input
        type="text"
        name="lastName"
        value={this.state.lastName}
        onChange={this.handleChange}
      />
    </label>
  </form>
      </div>
    );
  }
}

export default LoginPage;
