import React from "react";
import Prompt from "./Prompt";
import CardMapper from "./CardMapper";

var uniqueIDs = [
  //0
  { type: "character", name: "Miss Scarlet", image: "C1" },
  //1
  { type: "character", name: "Mr. Green", image: "C2" },
  //2
  { type: "character", name: "Colonel Mustard", image: "C3" },
  //3
  { type: "character", name: "Prof. Plum", image: "C4" },
  //4
  { type: "character", name: "Mrs. Peacock", image: "C5" },
  //5
  { type: "character", name: "Mrs. White", image: "C6" },
  //6
  { type: "weapon", name: "Candlestick", image: "W1" },
  //8
  { type: "weapon", name: "Knife", image: "W2" },
  //9
  { type: "weapon", name: "Pipe", image: "W3" },
  //7
  { type: "weapon", name: "Revolver", image: "W4" },
  //10
  { type: "weapon", name: "Rope", image: "W5" },
  //11
  { type: "weapon", name: "Wrench", image: "W6" },
  //12
  { type: "room", name: "Study", roomId: 11 },
  //13
  { type: "room", name: "Hall", roomId: 12 },
  //14
  { type: "room", name: "Lounge", roomId: 13 },
  //15
  { type: "room", name: "Dinning Room", roomId: 23 },
  //16
  { type: "room", name: "Billiard Room", roomId: 22 },
  //17
  { type: "room", name: "Library", roomId: 21 },
  //18
  { type: "room", name: "Conservatory", roomId: 31 },
  //19
  { type: "room", name: "Ballroom", roomId: 32 },
  //20
  { type: "room", name: "Kitchen", roomId: 33 },
];

class Prompts extends React.Component {
  constructor() {
    super();
    this.state = {
      show_cards: true,
      cards: [1, 8, 12, 13, 14],
      show_revoked: false,
      revoked_message: {},
      show_end: false,
      end_message: {},
    };
    this.displayCards = this.displayCards.bind(this)
    this.displayRevoked = this.displayRevoked.bind(this)
    this.displayEndGame = this.displayEndGame.bind(this)
  }

  displayCards() {
    console.log("displaycards")
    let items = this.state.cards.map((card) => {
      CardMapper.generateIdMap();
      var imgLocation = "./CardImages/" + CardMapper.getCardImgById(card);
      return (
        <img src={imgLocation} width="120" height="150"/>
      );
    }, this);
    //let items = <p>{JSON.stringify(this.state.cards)}</p>
    return (
      <div>
        <p>The game has started! Here are your cards:</p>
        {items}
      </div>
    );
  }

  displayRevoked() {
    CardMapper.generateIdMap();
    var room =
      "./CardImages/" +
      CardMapper.getCardImgById(
        this.state.revoked_message.message.correct_room
      );
    var weapon =
      "./CardImages/" +
      CardMapper.getCardImgById(
        this.state.revoked_message.message.correct_weapon
      );
    var character =
      "./CardImages/" +
      CardMapper.getCardImgById(
        this.state.revoked_message.message.correct_character
      );
    return (
      <div>
        <p>Revoked! Here's the envelope cards: </p>
        <img src={room} />
        <img src={weapon} />
        <img src={character} />
      </div>
    );
  }

  displayEndGame() {
    CardMapper.generateIdMap();
    var room =
      "./CardImages/" +
      CardMapper.getCardImgById(
        this.state.end_message.message.correct_room
      );
    var weapon =
      "./CardImages/" +
      CardMapper.getCardImgById(
        this.state.end_message.message.correct_weapon
      );
    var character =
      "./CardImages/" +
      CardMapper.getCardImgById(
        this.state.end_message.message.correct_character
      );
    return (
      <div>
        <p>Player {this.state.end_message.message.winning_player} won! Here's the envelope cards:</p>
        <img src={room} />
        <img src={weapon} />
        <img src={character} />
      </div>
    );
  }

  static getDerivedStateFromProps(props, state) {
    var newcards = state.cards;
    var newshowcards = state.show_cards;
    var newshowrevoked = state.show_revoked;
    var newrevokedmessage = state.revoked_message;
    var newshowend = state.show_end;
    var newendmessage = state.end_message;

    // if (props.cards != undefined) {
    //   if (props.cards != state.cards) {
    //     console.log("PromptCards");
    //     newcards = props.cards;
    //     newshowcards = true;
    //   }
    // }

    var first = props.actions[0];
    if (first != undefined) {
      if (first.message_type == 52) {
        // “accusation_correct”:
        // “correct_room”:
        // “correct_character”:
        // “correct_weapon”:
        if (first.message.accusation_correct === false) {
          console.log("PromptRevoked");
          newshowrevoked = true;
          newrevokedmessage = first;
        }
      } else if (first.message_type == 61) {
        //   “game_over”:
        //   “winning_player”:
        //   “correct_room”:
        //   “correct_character”:
        //   “correct_weapon”:
        console.log("PromptEnd");
        newshowend = true;
        newendmessage = first;
      }
    }

    return {
      cards: newcards,
      show_cards: newshowcards,
      show_revoked: newshowrevoked,
      revoked_message: newrevokedmessage,
      show_end: newshowend,
      end_message: newendmessage,
    };
  }

  render() {
    return (
      <div>
        {this.state.show_cards ? (
          <Prompt>
            {this.displayCards()}
          </Prompt>
        ) : null}
        {this.state.show_revoked ? (
          <Prompt>
            <h4>REVOKED</h4>
            {this.displayRevoked()}
          </Prompt>
        ) : null}
        {this.state.show_end ? (
          <Prompt>
            <h4>END GAME</h4>
            {this.displayEndGame()}
          </Prompt>
        ) : null}
      </div>
    );
  }
}
export default Prompts;

