import React from "react";
class Popup extends React.Component {
  render() {
    return (
      <div className="popup">
        <div className="popup_inner">
          <h1>{this.props.text}</h1>
          <button onClick={this.props.closePopup}>close me</button>
        </div>
      </div>
    );
  }
}
class Test extends React.Component {
  constructor() {
    super();
    this.state = {
      showPopup: false,
    };
    this.togglePopup = this.togglePopup.bind(this);
  }

  togglePopup() {
    if (this.state.showPopup === true) {
      this.setState({
        showPopup: false,
      });
    }
  }

  static getDerivedStateFromProps(props, state) {
    // <EndGamePrompt final_message={this.state.final_message} show={this.state.show}/>
    console.log("TESTPOPUP");
    console.log(JSON.stringify(props));
    console.log(JSON.stringify(state));

    var newshowPopup = state.showPopup;
    if (props.show === true) {
      newshowPopup = true;
    }

    return { showPopup: newshowPopup };
  }

  render() {
    return (
      <div className="app">
        <h1>hihi</h1>
        <button onClick={this.togglePopup()}>show popup</button>

        {this.state.showPopup ? (
          <Popup text="Close Me" closePopup={this.togglePopup()} />
        ) : null}
      </div>
    );
  }
}
export default Test;
