import React from "react";
import {
  makeMovement,
  makeSuggestion,
  makeAccusation,
  makeDisprove,
} from "./ClientManager";

class LoginPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      response: "",
      inputs: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    if (this.state.value === "") {
      alert("Don't forget to type an action!");
      return;
    }

    // Notify User that their action was registered.
    let userInput = this.state.value;
    this.setState({ response: userInput });

    alert("User Input: " + this.state.value);
    console.log("User Input: " + this.state.value);
    this.setState({
      value: "",
    });
    event.preventDefault();
  }

  createSelectItems(type) {
    let items = this.state.playerHand.map((item, i) => {
      return (
        item.type == type && (
          <option key={i} value={i}>
            {item.name}
          </option>
        )
      );
    }, this);
    return items;
  }

  render() {
    return (
      <React.Fragment>
                <select
          name="GuessedUser"
          id="GuessedUser"
          style={{ margin: "10px", marginLeft: "0px" }}
        >
          {this.createSelectItems("character")}
        </select>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            size="50"
            value={this.state.value}
            onChange={this.handleChange}
          />
          <input type="submit" value="Submit" />
        </form>
        <p> </p>
        <div>
          <h4>Previous Inputs</h4>
          <ul>
            {this.state.inputs.map((elem) => (
              <li>{elem}</li>
            ))}
          </ul>
        </div>
      </React.Fragment>
    );
  }
}
export default LoginPage;
