import React, { Component } from 'react';

class Message extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: '',
  };

  componentWillUnmount() {
    clearInterval(this.timer)
    this.timer = null;
  }


  getItems() {
    alert("about to fetch")
    fetch('http://node-express-env.eba-m3dz2nxw.us-east-2.elasticbeanstalk.com/test')
      .then(result => this.setState({ response: result.status}));

      // fetch('/api/hello')
      // .then(result => result.json())
      // .then(result => this.setState({ response: result.express + result.number }));
  }

  componentDidMount() {
    this.getItems();
    this.timer = setInterval(()=> this.getItems(), 5000);

    // this.callApi()
    //   .then(res => this.setState({ response: res.express }))
    //   .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/hello');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

render() {
    return (
        <p>!{this.state.response}!</p>
    );
  }
}

export default Message;
