import React, { Component } from 'react';

class Message extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: '',
  };

  componentWillUnmount() {
    clearInterval(this.timer)
    this.timer = null;
  }


  getItems() {
    //alert("about to fetch")
    fetch('http://node-express-env.eba-m3dz2nxw.us-east-2.elasticbeanstalk.com/test')
      .then(result => this.setState({ response: result.status}));

      // fetch('/api/hello')
      // .then(result => result.json())
      // .then(result => this.setState({ response: result.express + result.number }));
  }

  componentDidMount() {
    this.getItems();
    this.timer = setInterval(()=> this.getItems(), 5000);

  }

render() {
    return (
        <p>!{this.state.response}!</p>
    );
  }
}

export default Message;
