import React from "react";
import CardMapper from "./CardMapper";
import "./PlayerHand.css";

class PlayerHand extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      //cards: this.props.cards,
      cards: [],
    };
  }

  state = {
    cards: [],
    //[1, 2, 3, 4, 5], // Default. Can remove this once we know the cards are being populated.
  };

  updatePlayerHand = this.state.cards.map((card) => {
    console.log("UPDATE")
    CardMapper.generateIdMap();
    var imgLocation = "./CardImages/" + CardMapper.getCardImgById(card);
    return (
      <li>
        <div class="block">
          <img src={imgLocation} />
        </div>
      </li>
    );
  });

  static getDerivedStateFromProps(props, state) {

    var newcards = state.cards;
    if (props.cards != undefined) {
      if (props.cards != state.cards) {
        newcards = props.cards;
        console.log("CARDS " + props.cards);
        console.log("CARDS " + state.cards);
      }
    }
    return { cards: newcards };
  }

  displayPlayerHand () {
    return (
      <ul class="hs center">{this.updatePlayerHand}</ul>
    )
  }

  render() {
    console.log("PLAYERHAND"  + this.state.cards)
    //console.log("PLAYERHAND"  + this.state.cards.length)
    return (
      <div class="playerHandFrame">
        <ul class="hs center">{this.updatePlayerHand}</ul>
      </div>
    );
  }
}

export default PlayerHand;
