import React from "react";
import relaxed from "./Music/relaxed.wav";
import electronic from "./Music/electronic.wav";
import marimba from "./Music/marimba.wav";
import dreamy from "./Music/dreamy.wav";
import dreamy_2 from "./Music/dreamy_2.wav";


import 'bootstrap/dist/css/bootstrap.min.css';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown'




class Music extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            play: false,
            pause: true,
        }


        this.audio = new Audio(electronic);

        this.audio.addEventListener('ended', function () {
            this.currentTime = 0;
            this.play();
        }, false);

    }

    play = () => {
        this.setState({ play: true, pause: false })
        this.audio.play();
    }

    pause = () => {
        this.setState({ play: false, pause: true })
        this.audio.pause();
    }




    render() {

        return (
            <div>
                <button onClick={this.play}>Play</button>
                <button onClick={this.pause}>Pause</button>

                <select id="dropdown">
                    <option value="N/A">N/A</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                </select>


            </div>
        );
    }

}

