import React from 'react';
//import { grid } from '../data/grid.js';
import { Square } from './Square';

var roomCode = ['11','12','13','21','22','23','31','32','33',
'1112','1213','1121','1222','1323','2122','2223','2131','2232','2333','3132','3233']

//All rooms and Hallways locations to put objects. Sequence is important - Please don't change them.
var roomArra = {
  "11": [103,241,273,426], 
  "12": [379,300,585,455],
  "13": [691,235,853,394],
  "21": [103,541,337,716],
  "22": [423,544,561,715],
  "23": [674,510,828,660],
  "31": [103,835,305,1011],
  "32": [385,796,569,995],
  "33": [640,892,861,1017],
  "1112": [304,374,304,374],
  "1213": [625,374,625,374],
  "1121": [219,460,219,460],
  "1222": [543,479,543,479],
  "1323": [735,440,735,440],
  "2122": [365,599,365,599],
  "2223": [591,567,591,567],
  "2131": [284,753,284,753],
  "2232": [444,741,444,741],
  "2333": [680,725,680,725],
  "3132": [335,924,335,924],
  "3233": [590,923,590,923]
}

//All valid rooms and hallways to move in (based on player's current location). Sequence is important - Please don't change them.
var roomAccess = {
    "11": ['1112','1121','33'],
    "12": ['1112','1222','1213'], 
    "13": ['1213','1323','31'],
    "21": ['1121','2122','2131'],
    "22": ['2122','1222','2223','2232'],
    "23": ['2223','1323','2333'],
    "31": ['2131','3132','13'],
    "32": ['3132','2232','3233'],
    "33": ['3233','2333','11'],
    "1112": ['11','12'],
    "1213": ['12','13'],
    "1121": ['11','21'],
    "1222": ['12','22'],
    "1323": ['13','23'],
    "2122": ['21','22'],
    "2223": ['22','23'],
    "2131": ['21','31'],
    "2232": ['22','32'],
    "2333": ['23','33'],
    "3132": ['31','32'],
    "3233": ['32','33']
}

export class TicTac extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      dim:3,
      grid:Array(5).fill(0).map(x=>Array(5).fill("+")),
      player:'X',
      winner:null,
      active:true,

          // Stores players current locID
      players: [
      "2333", //Miss Scarlet
      "3233", //Mr. Green
      "2131", //Colonel Mustard
      "1121", //Prof. Plum
      "1112", //Mrs. Peacock
      "1323" //Mrs. White
      ],

    // Stores weapons current locID
      weapons: [
      "11",
      "13",
      "22",
      "23",
      "31",
      "32"
      ]

    };
    this.handleOnClick = this.handleOnClick.bind(this);
    this.checkWins = this.checkWins.bind(this);
    this.handleReset = this.handleReset.bind(this);
    this.dims = [parseFloat(500/this.state.grid.length), parseFloat(500/this.state.grid[0].length)]

  }

  handleReset(){
    const newGrid = Array(this.state.dim).fill(0).map(x=>Array(this.state.dim).fill("+"))
    this.setState({'grid':newGrid, 'player':'X'});
  }

  checkWins(x, y){
    const g = this.state.grid

    function checkDiagonal1(){
      if (x == y){
        const result = new Set(g.map((_, i)=>g[i][i]));
        announceWin(result);
      }
    }

    function checkDiagonal2(){
      if (x+y+1 == g.length){
        const result = new Set(g.map((_, i)=>g[i][g.length-1-i]))
        announceWin(result);
      }
    }

    function checkHorizontal(x){
      const result = new Set(g[x]);
      announceWin(result);
    }

    function checkVertical(y){
      const result = new Set(g.map((x)=>x[y]));
      announceWin(result);
    }

    function announceWin(l){
      if (l.size == 1){
        if (l.has("X")){
          setTimeout(()=>{alert("X wins")}, 10);
          return;
        } else {
          setTimeout(()=>{alert("O wins")}, 10);
          return;
        }
      }
    }

    checkDiagonal1();
    checkDiagonal2();
    checkHorizontal(x);
    checkVertical(y);
  }

  handleOnClick(x, y){
    const g = this.state.grid
    console.log("TicTac" + x + " " + y)
    if (this.state.active){
      if (g[x][y] == '+'){
        g[x][y] = this.state.player;
        this.setState({'grid':g});
        this.state.player = this.state.player == 'X' ? 'O':'X';
        this.checkWins(x, y);
    } else {
      alert('Please select an empty square!');
      }
    }
  }

  render(){
    const style = {
      margin:'auto',
      width: "auto",
      height:"auto",
      backgroundColor:'white',
      color:'white',
      fontSize:"3em",
      tableLayout:'fixed',
    }
    const rows = this.state.grid.map((r, i) => {return (
      <tr key={"row_"+i}>
        {r.map((d, j) => {console.log('building'); return(
          <Square
            key={i+"_"+j}
            dims={this.dims}
            onClick={()=>{this.handleOnClick(i,j)}}
            contents={d=="+"?" ":d}
            name="Austin" />
              )
            }
          )
        }
        </tr>)
        }
      );
    return (
      <div style={{textAlign:"center"}}>
        <h1>Tic-Tac-React!</h1>
        <small>tic-tac-toe, written with <b>ReactJS</b>. Enjoy!</small>
        <p>Current Player: {this.state.player}</p>
        <table cellSpacing="0" id="table" style={style}>
          <tbody>
            {rows}
          </tbody>
        </table>
        <br />
        {/* <button style={{margin:"auto"}} onClick={this.handleReset}>reset</button>
        <br /><br />
        <button onClick={()=>{this.state.dim==1?1:this.state.dim-=1;this.setState({dim:this.state.dim})}}>-</button>

            &nbsp;&nbsp;&nbsp;<span style={{color:'white'}}>{this.state.dim}</span>&nbsp;&nbsp;&nbsp;

        <button onClick={()=>{this.state.dim+=1;this.setState({dim:this.state.dim})}}>+</button> */}
        <br /><br/><br/>
      </div>
  )
  }
}

export default TicTac;
