import React from "react";
import relaxed from "./Music/relaxed.wav";
import electronic from "./Music/electronic.wav";
import marimba from "./Music/marimba.wav";
import dreamy from "./Music/dreamy.wav";
import dreamy_2 from "./Music/dreamy_2.wav";



class Music extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            play: false,
            pause: true,
        }


        this.audio = new Audio(electronic);

        this.audio.addEventListener('ended', function () {
            this.currentTime = 0;
            this.play();
        }, false);

    }

    play = () => {
        this.setState({ play: true, pause: false })
        this.audio.play();
    }

    pause = () => {
        this.setState({ play: false, pause: true })
        this.audio.pause();
    }




    render() {

        return (
            <div>
                <button onClick={this.play}>Play</button>
                <button onClick={this.pause}>Pause</button>
                <fieldset>
                    <input type="radio" name="action" id="track" value="track" /><label for="track">Track Submission</label><br />
                    <input type="radio" name="action" id="event" value="event" /><label for="event">Events and Artist booking</label><br />
                    <input type="radio" name="action" id="message" value="message" /><label for="message">Message us</label><br />
                </fieldset>
            </div>
        );
    }

}

