import React from "react";
class Popup extends React.Component {
  render() {
    return (
      <div className="popup">
        <div className="popup_inner">
          <br></br>
          {this.props.children}
          <br></br>
          <button onClick={this.props.closePopup}>Close</button>
        </div>
      </div>
    );
  }
}
class Prompt extends React.Component {
  constructor() {
    super();
    this.state = {
      show: true,
      close: true,
    };
    this.togglePopup = this.togglePopup.bind(this);
  }
  togglePopup() {
    console.log("TOGGLEPOPUP");
    this.setState({
      //showPopup: !this.state.showPopup,
      show: false,
      close: true,
    });
  }
  static getDerivedStateFromProps(props, state) {
    var newcards = state.cards;
    var newshowcards = state.show_cards;
    var newshowrevoked = state.show_revoked;
    var newrevokedmessage = state.revoked_message;
    var newshowend = state.show_end;
    var newendmessage = state.end_message;

    if (props.cards != undefined) {
      if (props.cards != state.cards) {
        console.log("PromptCards")
        newcards = props.cards;
        newshowcards = true;
      }
    }

    var first = props.actions[0];
    if (first != undefined) {
      if (first.message_type == 52) {
        // “accusation_correct”:
        // “correct_room”:
        // “correct_character”:
        // “correct_weapon”:
        if (first.message.accusation_correct === false) {
          console.log("PromptRevoked")
          newshowrevoked = true;
          newrevokedmessage = first;
        }
      } else if (first.message_type == 61) {
        //   “game_over”:
        //   “winning_player”:
        //   “correct_room”:
        //   “correct_character”:
        //   “correct_weapon”:
        console.log("PromptEnd")
        newshowend = true;
        newendmessage = first;
      }
    }

    return {
      cards: newcards,
      show_cards: newshowcards,
      show_revoked: newshowrevoked,
      revoked_message: newrevokedmessage,
      show_end: newshowend,
      end_message: newendmessage,
    };
  }

  render() {
    return (
      <div>
        //Initial Cards
        {this.state.show_cards ? (
          <Prompt>
            <h4>SHOW CARDS</h4>
          </Prompt>
        ) : null}
        //Revoked
        {this.state.show_revoked ? (
          <Prompt>
            <h4>REVOKED</h4>
          </Prompt>
        ) : null}
        //End Game
        {this.state.show_end ? (
          <Prompt>
            <h4>END GAME</h4>
          </Prompt>
        ) : null}
      </div>
    );
  }
}
export default Prompt;

