import React from "react";
import {
  makeMovement,
  makeSuggestion,
  makeAccusation,
  makeDisprove,
} from "./ClientManager";

class LoginPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      response: "",
      inputs: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    if (this.state.value === "") {
      alert("Don't forget to type an action!");
      return;
    }

    // Notify User that their action was registered.
    let userInput = this.state.value;
    this.setState({ response: userInput });

    alert("User Input: " + this.state.value);
    console.log("User Input: " + this.state.value);
    this.setState({
      value: "",
    });
    event.preventDefault();
  }


  render() {
    return (
      <React.Fragment>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            size="50"
            value={this.state.value}
            onChange={this.handleChange}
          />
          <input type="submit" value="Submit" />
        </form>
        <p> </p>
        <div>
          <h4>Previous Inputs</h4>
          <ul>
            {this.state.inputs.map((elem) => (
              <li>{elem}</li>
            ))}
          </ul>
        </div>
      </React.Fragment>
    );
  }
}
export default LoginPage;
